  `method` varchar(1000) DEFAULT NULL COMMENT '制作方法',
  `good` varchar(200) DEFAULT NULL COMMENT '展示简介',
  PRIMARY KEY (`id`),
  UNIQUE KEY `medicated_food_id_uindex` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
